mapper.update:
update partner_content
set pc_content = #{pcContent},
pc_file = #{pcFile},
pc_idx = #{pcIdx},
cm_music = #{cmMusic}
where pc_idx = #{pcIdx}

mapper.select:
SELECT pd.pd_idx, pd.user_id1, pd.user_id2, up.profile_img,
up.user_tag1, up.user_tag2, up.user_tag3, pd.progress
FROM
partner_detail pd
JOIN user_profile up ON pd.user_id2 = up.user_id
WHERE CASE
WHEN pd.user_id1 = #{userId1} THEN pd.user_id1 = #{userId1}
WHEN pd.user_id2 = #{userId1} THEN pd.user_id2 = #{userId1}
END;

mapper.update:
update partner_detail
set progress = '1'
where pd_idx = #{pdIdx}
